def find_correct_floor(arr):
    floor = 0
    for parentheses in arr:
        if parentheses == '(':
            floor += 1
        else:
            floor -= 1
    return floor


def first_basement(arr):
    floor = 0
    basement_position = 0
    for i, parentheses in enumerate(arr):
        basement_position = i
        if parentheses == '(':
            floor += 1
        elif parentheses == ')':
            floor -= 1
        if floor == -1:
            return basement_position + 1



